$("form").ready(function(){$("form").submit(function(n){n.preventDefault();$.ajax({url:"api/reservation",contentType:"application/json",method:"POST",data:JSON.stringify({clientName:this.elements.ClientName.value,location:this.elements.Location.value}),success:function(n){addTableRow(n)}})})});var addTableRow=function(n){$("table tbody").append("<tr><th>"+n.reservationId+"<\/td><th>"+n.clientName+"<\/td><th>"+n.location+"<\/td><\/th>")};